import "./App.css";
import Wrapper from "./components/Wrapper";
import Buttons from "./components/Buttons";
import Display from "./components/Display";
import React, { useState } from "react";

function App() {
  let [calc, setCalc] = useState({
    operator: "",
    calcNum: 0,
    result: 0,
  });

  const handleClear = () => {
    setCalc({
      operator: "",
      calcNum: 0,
      result: 0,
    });
  };

  const handleOperator = (sign) => {
    setCalc({
      operator: sign,
      result: calc.result + sign,
    });
  };

  const handleNumClick = (input) => {
    setCalc({
      calcNum: input,
      result: !calc.result ? input : calc.result + input,
    });
  };

  return (
    <Wrapper>
      <Display value={calc.calcNum ? calc.calcNum : calc.result} />
      <Buttons
        onNumClick={handleNumClick}
        onSignClick={handleOperator}
        on
        clear={handleClear}
      />
    </Wrapper>
  );
}

export default App;
