import "./App.css";
import Wrapper from "./components/Wrapper";
import Buttons from "./components/Buttons";
import Display from "./components/Display";
import React, { useState } from "react";

function App() {
  let [calc, setCalc] = useState({
    operator: "",
    calcNum: 0,
    result: 0,
  });

  const handleEquals = () => {
    setCalc({});
  };

  const handleClear = () => {
    setCalc({
      operator: "",
      calcNum: 0,
      result: 0,
    });
  };

  const handleOperator = (sign) => {
    setCalc({
      operator: sign,
      result: calc.calcNum + String(sign),
    });
  };

  const handleNumClick = (input) => {
    if (calc.calcNum.length < 16) {
      setCalc({
        ...calc,
        num:
          calc.num === 0 && input === "0"
            ? "0"
            : calc.calcNum % 1 === 0
            ? Number(calc.calcNum + input)
            : calc.calcNum + input,
        res: !calc.operator ? 0 : calc.result,
      });
    }
  };

  return (
    <React.Fragment>
      <h1 className="pagetitle">calculate </h1>
      <Wrapper>
        <Display value={calc.calcNum ? calc.calcNum : calc.result} />
        <Buttons
          onNumClick={handleNumClick}
          onSignClick={handleOperator}
          onClear={handleClear}
          onEquals={handleEquals}
        />
      </Wrapper>
      <h3 className="bottomtitle">//made by javi</h3>
    </React.Fragment>
  );
}

export default App;
