import "./App.css";
import Wrapper from "./components/Wrapper";
import Buttons from "./components/Buttons";
import Display from "./components/Display";
import React, { useState } from "react";

function App() {
  let [calc, setCalc] = useState({
    operator: "",
    calcNum: 0,
    result: 0,
  });

  const handleEquals = () => {
    setCalc({});
  };

  const handleClear = () => {
    setCalc({
      operator: "",
      calcNum: 0,
      result: 0,
    });
  };

  const handleOperator = (sign) => {
    setCalc({
      operator: sign,
      result: calc.result + sign,
    });
  };

  const handleNumClick = (input) => {
    setCalc({
      calcNum: "input" + toString(calc.calcNum),
      result: calc.result ? calc.result + input : input,
    });
  };

  return (
    <React.Fragment>
      <h1>calculate</h1>
      <Wrapper>
        <Display value={calc.result ? calc.result : calc.calcNum} />
        <Buttons
          onNumClick={handleNumClick}
          onSignClick={handleOperator}
          onClear={handleClear}
          onEquals={handleEquals}
        />
      </Wrapper>
    </React.Fragment>
  );
}

export default App;
