import "./App.css";
import Wrapper from "./components/Wrapper";
import Buttons from "./components/Buttons";
import Display from "./components/Display";
import React, { useState } from "react";

function App() {
  let [calc, setCalc] = useState({
    operator: "",
    calcNum: 0,
    result: 0,
  });

  const handleEquals = () => {
    if (calc.operator && calc.calcNum) {
      const math = (a, b, sign) =>
        sign === "+"
          ? a + b
          : sign === "-"
          ? a - b
          : sign === "X"
          ? a * b
          : a / b;

      setCalc({
        ...calc,
        res:
          calc.num === "0" && calc.sign === "/"
            ? "Can't divide with 0"
            : math(Number(calc.res), Number(calc.num), calc.sign),
        sign: "",
        num: 0,
      });
    }
  };

  const handleClear = () => {
    setCalc({
      operator: "",
      calcNum: 0,
      result: 0,
    });
  };

  const handleOperator = (sign) => {
    setCalc({
      ...calc,
      operator: sign,
      result: !calc.result && calc.calcNum ? calc.calcNum : calc.result,
    });
  };

  const handleNumClick = (input) => {
    input.preventDefault();
    if (calc.calcNum.length < 16) {
      setCalc({
        ...calc,
        calcNum:
          calc.calcNum === 0 && input === "0"
            ? "0"
            : calc.calcNum % 1 === 0
            ? Number(calc.calcNum + input)
            : calc.calcNum + input,
        result: !calc.operator ? 0 : calc.result,
      });
    }
  };

  return (
    <React.Fragment>
      <h1 className="pagetitle">calculate </h1>
      <Wrapper>
        <Display value={calc.calcNum ? calc.calcNum : calc.result} />
        <Buttons
          onNumClick={handleNumClick}
          onSignClick={handleOperator}
          onClear={handleClear}
          onEquals={handleEquals}
        />
      </Wrapper>
      <h3 className="bottomtitle">//made by javi</h3>
    </React.Fragment>
  );
}

export default App;
